con:config.script:
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
def holder = groovyUtils.getXmlHolder(context.Response)

holder.namespaces['ns1'] = 'http://speller.yandex.net/services/spellservice';

def amount = holder["//ns1:SpellResult/ns1:error/ns1:word"].size()
log.info('errors amount is ' + amount)
assert amount == 2

def codeError1 = holder["//ns1:SpellResult/ns1:error[1]/@code"]
log.info('codeError 1  = ' + codeError1)
def codeError2 = holder["//ns1:SpellResult/ns1:error[2]/@code"]
log.info('codeError 2  = ' + codeError2)

assert codeError1 =='1'
assert codeError2 =='1'

def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
def holder = groovyUtils.getXmlHolder(context.Response)
holder.namespaces['ns1'] = 'http://speller.yandex.net/services/spellservice';
def pos = holder["//ns1:SpellResult/ns1:error[.//ns1:word = 'word']/@pos"]

log.info('position = ' + pos)

assert pos == '13'

def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
def holder = groovyUtils.getXmlHolder(context.Response)
holder.namespaces['ns1'] = 'http://speller.yandex.net/services/spellservice';

assert holder["//ns1:SpellResult/ns1:error[.//ns1:word='маршьрутка']/ns1:s"] == 'маршрутка'
log.info("Assert passed")
